package fr.idformation.evaluation.core.dto;

import java.util.Set;

public class CustomerDTO {
	/**
	 * The customer DTO's autogenerated id number
	 */
	private Integer id;

	/**
	 * The customer DTO's first name
	 */
	private String firstName;

	/**
	 * The customer DTO's last name
	 */
	private String lastName;

	/**
	 * The customer DTO's adress, usually a number and the street name
	 */
	private String adress;

	/**
	 * The customer DTO's zip code , usually a 6 figure number
	 */
	private String zipCode;

	/**
	 * The customer DTO's country
	 */
	private String country;

	/**
	 * The customer DTO's contact List
	 */
	private Set<ContactDTO> contactDTOs;

	/**
	 * @return the customer DTO's id
	 */
	public Integer getId() {
		return id;
	}

	/**
	 * @param id the customer DTO's id to set
	 */
	public void setId(Integer id) {
		this.id = id;
	}

	/**
	 * @return the customer DTO's firstName
	 */
	public String getFirstName() {
		return firstName;
	}

	/**
	 * @param firstName the customer DTO's firstName to set
	 */
	public void setFirstName(String firstName) {
		this.firstName = firstName;
	}

	/**
	 * @return the customer DTO's lastName
	 */
	public String getLastName() {
		return lastName;
	}

	/**
	 * @param lastName customer DTO's the lastName to set
	 */
	public void setLastName(String lastName) {
		this.lastName = lastName;
	}

	/**
	 * @return the customer DTO's adress
	 */
	public String getAdress() {
		return adress;
	}

	/**
	 * @param adress customer DTO's the adress to set
	 */
	public void setAdress(String adress) {
		this.adress = adress;
	}

	/**
	 * @return the customer DTO's zipCode
	 */
	public String getZipCode() {
		return zipCode;
	}

	/**
	 * @param zipCode the customer DTO's zipCode to set
	 */
	public void setZipCode(String zipCode) {
		this.zipCode = zipCode;
	}

	/**
	 * @return the customer DTO's country
	 */
	public String getCountry() {
		return country;
	}

	/**
	 * @param country the customer DTO's country to set
	 */
	public void setCountry(String country) {
		this.country = country;
	}

	/**
	 * @return the DTO's contacts
	 */
	public Set<ContactDTO> getContacts() {
		return contactDTOs;
	}

	/**
	 * @param contacts the DTO's contacts to set
	 */
	public void setContacts(Set<ContactDTO> contactDTOs) {
		this.contactDTOs = contactDTOs;
	}

	/**
	 *
	 * @param contactDTO
	 */
	public void addContact(ContactDTO contactDTO) {
		this.contactDTOs.add(contactDTO);

	}

	/**
	 *
	 * @param contactDTO
	 */
	public void removeContact(ContactDTO contactDTO) {
		this.contactDTOs.remove(contactDTO);
	}

	/**
	 * The customer DTO's default contructor
	 */
	public CustomerDTO() {
		super();
	}

}
